import React, { useEffect, useState } from "react";
export default function shop_2() {
    var [test, setTest] = useState(false)
    var [product, setProduct] = useState([])

    useEffect(function() {
        if (!test) {
            fetchData()
        }
    }, [test])

    var fetchData = async function () {
        var res = await fetch('http://localhost:3001/Fruits')
        var data = await res.json()
        setProduct(prevFruit => [...prevFruit, ...data])
        setTest(true)
    }

    return(
        <>
            {
                product.map(item => {
                    return (
                        <div class="col-md-3 row g-4 justify-content-center">
                                        <div class="rounded position-relative fruite-item">
                                            <div class="fruite-img">
                                                <img src={item.img} class="img-fluid w-100 rounded-top" alt="" />
                                            </div>
                                            <div class="text-white bg-secondary px-3 py-1 rounded position-absolute" style={{top: '10px', left: '10px'}}>Fruits</div>
                                            <div class="p-4 border border-secondary border-top-0 rounded-bottom">
                                                <h4>{item.name}</h4>
                                                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit sed do eiusmod te incididunt</p>
                                                <div class="d-flex justify-content-between flex-lg-wrap">
                                                    <p class="text-dark fs-5 fw-bold mb-0">{item.price}/ kg</p>
                                                    <a href="#" class="btn border border-secondary rounded-pill px-3 text-primary"><i class="fa fa-shopping-bag me-2 text-primary"></i> Add to cart</a>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                    )
                })
            }
        </>
    )
}
